bool rgb_matrix_indicators_kb(void) {
    if (!rgb_matrix_indicators_user()) {
        return false;
    }

    if (host_keyboard_led_state().caps_lock) {
        rgb_matrix_set_color(25, HSV_BLUE);
    } else if (!(rgb_matrix_get_flags() & LED_FLAG_INDICATOR)) {
        rgb_matrix_set_color(25, HSV_BLACK);
    }

    if (host_keyboard_led_state().scroll_lock) {
        rgb_matrix_set_color(73, HSV_BLUE);
    } else if (!(rgb_matrix_get_flags() & LED_FLAG_INDICATOR)) {
        rgb_matrix_set_color(73, HSV_BLACK);
    }
    return true;
}

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case SOCD_A:
            if (record->event.pressed) {
                a_down = true;
                // A is pressed, ignore D
                unregister_code(KC_D);
                register_code(KC_A);
            } else {
                a_down = false;
                // A is lifted
                unregister_code(KC_A);
                if (d_down) {
                    register_code(KC_D);
                }
            }
            return false;
        case SOCD_D:
            if (record->event.pressed) {
                d_down = true;
                // D is pressed, ignore A
                unregister_code(KC_A);
                register_code(KC_D);
            } else {
                d_down = false;
                // D is lifted
                unregister_code(KC_D);
                if (a_down) {
                    register_code(KC_A);
                }
            }
            return false;
        case RAPIDF:
            if (record->event.pressed) {
                rapidfire = true;
                rapidfire_timer = timer_read32();
            } else {
                rapidfire = false;
            }
    }
    return true;
}

void matrix_scan_user(void) {
    if (rapidfire && timer_elapsed32(rapidfire_timer) > RAPIDFIRE_DELAY) {
        tap_code(KC_MS_BTN1);
        rapidfire_timer = timer_read32(); // reset timer
    }
}

